import { StyleSheet, Dimensions } from "react-native";
const width = Dimensions.get('window').width;

const styles = StyleSheet.create({
    container: {
      alignItems: "center",
      alignSelf: 'stretch',
      flexDirection: "row",
      flexWrap: 'wrap',
      padding: 150
    },
    text: {
        color: "white",
        fontSize: 120,
      },
    textContainer:  {
        width: 700
    }
  });
if(width<800){
styles = StyleSheet.create({
    container: {
      alignItems: "center",
      alignSelf: 'stretch',
      flexDirection: "row",
      flexWrap: 'wrap',
      padding: 150
    },
    text: {
        color: "white",
        fontSize: 120,
      },
    textContainer:  {
        width: 700
    }
  });
}

export default styles
